  input:-ms-input-placeholder,
  textarea:-ms-input-placeholder {
    color: ${placeholderColor};
  }

  input::-ms-input-placeholder,
  textarea::-ms-input-placeholder {
    color: ${placeholderColor};
  }

  input::placeholder,
  textarea::placeholder {
    color: ${placeholderColor};
  }
`;

