type Direction = 'up' | 'down' | 'left' | 'right';

interface ChevronProps extends BoxProps {
  direction?: Direction;
}

const rotate = (direction: Direction = 'right') => {
  switch (direction) {
    case 'left':
      return '90';
    case 'up':
      return '180';
    case 'right':
      return '270';
    case 'down':
      return 0;
    default:
      throw new Error('`rotate` must receive direction parameter');
  }
};

