  //@ts-ignore
  // eslint-disable-next-line no-param-reassign
  path = (path?.split?.('.') ?? [path]) as string;
  // eslint-disable-next-line no-param-reassign
  for (index = 0; index < path.length; index++) {
    // eslint-disable-next-line no-param-reassign
    obj = obj ? obj[path[index]] : undefined;
  }
  return obj === undefined ? fallback : obj;
