  const updateValue = React.useCallback(
    (next: React.SetStateAction<T>) => {
      const nextValue = runIfFn(next, value);
      const shouldUpdateState = shouldUpdate(value, nextValue);

      if (!shouldUpdateState) return;

      if (!isControlled) {
        setValue(next);
      }

